const deleteApprovalHierarchy = async id => {
  await client.query('DELETE FROM approval_hierarchies WHERE id = $1', [id]);
};

const getLeaveRequests = async () => {
  const res = await client.query('SELECT * FROM leave_requests');
  return res.rows;
};

const getLeaveRequestById = async id => {
  const res = await client.query('SELECT * FROM leave_requests WHERE id = $1', [id]);
  return res.rows[0];
};

const saveLeaveRequest = async ({ userId, leaveType, startDate, endDate, status }) => {
  const res = await client.query(
    'INSERT INTO leave_requests (user_id, leave_type, start_date, end_date, status) VALUES ($1, $2, $3, $4, $5) RETURNING *',
    [userId, leaveType, startDate, endDate, status]
  );
  return res.rows[0];
};

const updateLeaveRequest = async ({ id, userId, leaveType, startDate, endDate, status }) => {
  const res = await client.query(
    'UPDATE leave_requests SET user_id = $1, leave_type = $2, start_date = $3, end_date = $4, status = $5 WHERE id = $6 RETURNING *',
    [userId, leaveType, startDate, endDate, status, id]
  );
  return res.rows[0];
};

const deleteLeaveRequest = async id => {
