                    Console.WriteLine( Result:   + result);
                    break;
                case '-':
                    result = num1 - num2;
                    Console.WriteLine( Result:   + result);
                    break;
                case '*':
                    result = num1 * num2;
                    Console.WriteLine( Result:   + result);
